"use strict";(self.webpackChunkhwchiu=self.webpackChunkhwchiu||[]).push([[92372],{93423:e=>{e.exports=JSON.parse('{"label":"DNS","permalink":"/docs/tags/dns","allTagsPath":"/docs/tags","count":4,"items":[{"id":"techPost/2018/kubernetes-dns","title":"[Kubernetes] DNS setting in your Pod","description":"DNS \u5728\u50b3\u7d71\u7684\u7db2\u8def\u67b6\u69cb\u4e2d\u4e00\u76f4\u626e\u6f14\u5f88\u91cd\u8981\u7684\u89d2\u8272\uff0c\u53ef\u4ee5\u8b93\u7528\u6236\u7aef\u900f\u904e FQDN \u7684\u65b9\u5f0f\u53bb\u5b58\u53d6\u76ee\u6a19\u7684\u4f3a\u670d\u5668\u7aef\uff0c\u4e0d\u9700\u8981\u53bb\u5beb\u6b7b\u5c0d\u65b9\u7684 IP \u5730\u5740\u3002\u7136\u800c\u5728 kubernetes \u7684\u67b6\u69cb\u4e2d, kubernetes \u9810\u8a2d\u5c31\u6703\u5efa\u7acb\u4e00\u5957 DNS server \u8b93\u6240\u6709\u5275\u5efa\u7684 Pod \u4f86\u4f7f\u7528\u3002\u5c0d\u65bc\u4e00\u822c\u7684\u4f7f\u7528\u8005\u4f86\u8aaa\uff0c\u53ea\u8981\u80fd\u5920\u5b58\u53d6\u5916\u9762\u7db2\u8def\u4ee5\u53ca Kubernetes Service \u76f8\u95dc\u5373\u53ef,\u7136\u800c\u5728\u67d0\u4e9b\u7279\u6b8a\u7684\u61c9\u7528\u60c5\u5883\u4e0b\uff0c\u8b6c\u5982\u76ee\u524d\u558a\u884c\u4e4b\u6709\u5e74\u7684 NFV \u67b6\u69cb\u4e2d\uff0c\u6211\u5011\u7684\u670d\u52d9(Pod)\u672c\u8eab\u53ef\u80fd\u6703\u9700\u8981\u66f4\u8907\u96dc\u7684\u7db2\u8def\u67b6\u69cb\uff0c\u8b6c\u5982\u540c\u6642\u5b58\u5728 Data Network/Control Network. \u9019\u60c5\u6cc1\u4e0b\uff0c\u6211\u5011\u7684 Pod \u6703\u9700\u8981\u7279\u5225\u53bb\u8655\u7406\u81ea\u5df1\u88e1\u9762\u670d\u52d9\u6240\u4f7f\u7528\u7684 DNS Server \u3002 \u672c\u6587\u4e3b\u8981\u91dd\u5c0d Pod \u88e1\u9762\u95dc\u65bc DNS \u76f8\u95dc\u7684\u8a2d\u5b9a\u9032\u884c\u4ecb\u7d39\uff0c\u4e26\u4e14\u900f\u904e\u5be6\u969b\u90e8\u5c6c Yaml \u7684\u65b9\u5f0f\u4f86\u770b\u770b\u5230\u5e95\u5982\u4f55\u4f7f\u7528\u3002","permalink":"/docs/techPost/2018/kubernetes-dns"},{"id":"techPost/2018/kubernetes-dns-ii","title":"[Kubernetes] DNS Setting When DnsPolicy Is Default","description":"\u672c\u6587\u8ddf\u5927\u5bb6\u5206\u4eab\u4e00\u500b\u5728\u5be6\u969b\u90e8\u5c6c\u4e0a\u9047\u5230\u7684\u554f\u984c\uff0c\u5728\u4e0d\u540c\u7684\u74b0\u5883\u4e0b\uff0c\u63a1\u7528 dnsPolicy:Default \u8a2d\u5b9a\u7684 Kubernetes Pod \u88e1\u9762\u6240\u8a2d\u5b9a\u7684 DNS Server \u537b\u662f\u5b8c\u5168\u4e0d\u540c\u7684. \u6839\u64da\u5be6\u969b\u7814\u7a76\u8207\u89c0\u5bdf\u5f8c\uff0c\u767c\u73fe\u9019\u500b\u6578\u5b57\u4e26\u4e0d\u662f\u55ae\u55ae\u7684\u4f9d\u9760 kubernetes \u53bb\u8655\u7406\uff0c\u5be6\u969b\u4e0a\u4e5f\u8ddf Docker \u672c\u8eab\u5982\u4f55\u53bb\u8a2d\u5b9a\u5bb9\u5668\u7684 dns \u4e5f\u6709\u95dc\u4fc2\uff0c\u9019\u90e8\u5206\u5c31\u5305\u542b\u4e86\u5bbf\u4e3b\u6a5f\u7684 /etc/resolv.conf \u4ee5\u53ca\u5bbf\u4e3b\u6a5f\u4e0a Docker \u904b\u884c\u6642\u7684\u53c3\u6578. \u672c\u6587\u6703\u5148\u4ecb\u7d39\u9019\u500b\u554f\u984c\uff0c\u4e26\u4e14\u5206\u4eab\u89e3\u6c7a\u554f\u984c\u7684\u601d\u8def\u4ee5\u81f4\u65bc\u6700\u5f8c\u53ef\u4ee5\u5f97\u5230\u9019\u500b\u7d50\u8ad6\u3002","permalink":"/docs/techPost/2018/kubernetes-dns-ii"},{"id":"techPost/2018/kubernetes-dns-iii","title":"[Kubernetes] DNS Setting with Dockerd(\u539f\u59cb\u78bc\u5206\u6790\u4e0a)","description":"\u5728\u524d\u7bc7\u6587\u7ae0\u6709\u8ddf\u5927\u5bb6\u5206\u4eab\u904e\u5be6\u969b\u90e8\u5c6c\u4e0a\u9047\u5230\u7684 DNS \u554f\u984c\uff0c\u4e26\u4e14\u900f\u904e\u5be6\u9a57\u4f50\u8b49\u53bb\u89c0\u5bdf\u7d50\u679c, \u672c\u7bc7\u6587\u7ae0\u5247\u662f\u900f\u904e\u53e6\u5916\u4e00\u7a2e\u89c0\u9ede\u4f86\u89c0\u5bdf\u7d50\u679c,\u900f\u904e\u95b1\u8b80\u539f\u59cb\u78bc\u7684\u65b9\u5f0f\u4f86\u89c0\u5bdf\u5230\u5e95 kubernetes \u518d\u5275\u5efa\u76f8\u95dc\u7684 Pod \u6642\u6703\u5982\u4f55\u53bb\u8655\u7406 DNS \u76f8\u95dc\u7684\u8a2d\u5b9a\uff0c\u7531\u65bc\u6574\u500b\u904e\u7a0b\u727d\u626f\u5230 kubernetes \u4ee5\u53ca CRI(Container Runtime Interface)\u7684\u64cd\u4f5c\uff0c\u800c\u6211\u5011\u9810\u8a2d\u4f7f\u7528\u7684\u5247\u662f Docker \u4f5c\u70ba\u5e95\u5c64\u5bb9\u5668\u7684\u64cd\u4f5c. \u7531\u65bc\u7bc7\u5e45\u904e\u9577\uff0c\u6240\u4ee5\u672c\u6587\u6703\u8457\u91cd\u65bc kubernetes \u539f\u59cb\u78bc\u7684\u90e8\u5206\uff0c\u800c Docker \u76f8\u95dc\u7684\u90e8\u5206\u5247\u6703\u9918\u4e0b\u7bc7\u6587\u7ae0\u4f86\u7814\u7a76.","permalink":"/docs/techPost/2018/kubernetes-dns-iii"},{"id":"techPost/2018/kubernetes-dns-iiii","title":"[Kubernetes] DNS Setting with Dockerd(\u539f\u59cb\u78bc\u5206\u6790\u4e0b)","description":"\u5728\u524d\u7bc7\u6587\u7ae0\u6709\u8ddf\u5927\u5bb6\u5206\u4eab\u904e\u5be6\u969b\u90e8\u5c6c\u4e0a\u9047\u5230\u7684 DNS \u554f\u984c\uff0c\u4e26\u4e14\u900f\u904e\u5be6\u9a57\u4f50\u8b49\u53bb\u89c0\u5bdf\u7d50\u679c, \u672c\u7bc7\u6587\u7ae0\u5247\u662f\u900f\u904e\u53e6\u5916\u4e00\u7a2e\u89c0\u9ede\u4f86\u89c0\u5bdf\u7d50\u679c,\u900f\u904e\u95b1\u8b80\u539f\u59cb\u78bc\u7684\u65b9\u5f0f\u4f86\u89c0\u5bdf\u5230\u5e95 kubernetes \u518d\u5275\u5efa\u76f8\u95dc\u7684 Pod \u6642\u6703\u5982\u4f55\u53bb\u8655\u7406 DNS \u76f8\u95dc\u7684\u8a2d\u5b9a\uff0c\u7531\u65bc\u6574\u500b\u904e\u7a0b\u727d\u626f\u5230 kubernetes \u4ee5\u53ca CRI(Container Runtime Interface)\u7684\u64cd\u4f5c\uff0c\u800c\u6211\u5011\u9810\u8a2d\u4f7f\u7528\u7684\u5247\u662f Docker \u4f5c\u70ba\u5e95\u5c64\u5bb9\u5668\u7684\u64cd\u4f5c. \u672c\u7bc7\u6587\u7ae0\u6703\u91dd\u5c0d\u5f8c\u534a\u90e8\u5206\uff0c\u4e5f\u5c31\u662f\u6240\u8b02\u7684 docker(dockerd) \u672c\u8eab\u518d\u5275\u5efa\u5bb9\u5668\u7684\u6642\u5019\uff0c\u6703\u5982\u4f55\u8655\u7406\u5176 DNS \u76f8\u95dc\u7684\u8a2d\u5b9a\uff0c\u900f\u904e\u95b1\u8b80 docker-ce \u4ee5\u53ca libnetwork \u76f8\u95dc\u7684\u539f\u59cb\u78bc\uff0c\u4e0d\u4f46\u80fd\u5920\u66f4\u6e05\u695a\u7684\u91d0\u6e05\u5168\u90e8\u7684\u904b\u4f5c\u539f\u7406\uff0c\u4e5f\u80fd\u5920\u5b78\u7fd2\u5230 docker \u5e95\u5c64\u5be6\u8e10\u7684\u904e\u7a0b\u8207\u7cbe\u795e","permalink":"/docs/techPost/2018/kubernetes-dns-iiii"}]}')}}]);