"use strict";(self.webpackChunkhwchiu=self.webpackChunkhwchiu||[]).push([[13712],{57189:e=>{e.exports=JSON.parse('{"label":"iptables","permalink":"/docs/tags/iptables","allTagsPath":"/docs/tags","count":9,"items":[{"id":"techPost/2018/kubernetes-service-ii","title":"[Kubernetes] How to Implement Kubernetes Service - ClusterIP","description":"\u5728\u524d\u8ff0\u4e2d\u6211\u5011\u5df2\u7d93\u5b78\u904e\u4e86\u4ec0\u9ebc\u662f kubernetes service, \u4e00\u822c\u60c5\u6cc1\u4e0b\u90fd\u6703\u63a1\u7528 ClusterIP \u7684\u5f62\u614b\u5e6b\u7279\u5b9a\u7684\u5bb9\u5668\u61c9\u7528\u7a0b\u5f0f\u63d0\u4f9b Service \u7684\u670d\u52d9. \u672c\u6587\u6703\u91dd\u5c0d ClusterIP \u7684\u6982\u5ff5\u9032\u884c\u66f4\u6df1\u5165\u7684\u63a2\u8a0e,\u4e26\u4e14\u5617\u8a66\u5f9e\u7cfb\u7d71\u5c64\u9762\u7684\u8a2d\u8a08\u8207\u61c9\u7528\u4f86\u7814\u7a76\u5230\u5e95 ClusterIP \u5e95\u5c64\u662f\u600e\u9ebc\u5be6\u4f5c\u7684,\u9019\u90e8\u5206\u7684\u5be6\u4f5c\u5305\u542b\u4e861) ClusterIP \u5230\u5e95\u5728\u90a3\u88e1\uff1f 2) \u5982\u679c\u6709\u591a\u500b Endpoints \u7684\u8a71, \u662f\u5982\u4f55\u9078\u64c7\u7576\u524d\u9023\u7dda\u7684\u6700\u7d42\u76ee\u6a19. \u9019\u4e9b\u7814\u7a76\u7684\u5167\u5bb9\u5305\u542b\u4e86\u5e38\u898b\u4e86\u7db2\u8def\u6982\u5ff5\uff0c\u5982 NAT(Network Address Translation) \u4ee5\u53ca iptables \u672c\u8eab\u7684\u8a2d\u8a08\u53ca\u4f7f\u7528\uff0c\u5982 table/chian \u7b49\u6982\u5ff5\u6709\u521d\u6b65\u7684\u8a8d\u77e5,\u9019\u6a23\u5c0d\u65bc\u672c\u6587\u7684\u63a2\u8a0e\u6703\u66f4\u660e\u767d\u8207\u77ad\u89e3.","permalink":"/docs/techPost/2018/kubernetes-service-ii"},{"id":"techPost/2018/kubernetes-service-iii","title":"[Kubernetes] How to Implement Kubernetes Service - NodePort","description":"\u5728\u524d\u8ff0\u4e2d\u6211\u5011\u5df2\u7d93\u5b78\u904e\u4e86\u4ec0\u9ebc\u662f kubernetes service \u4ee5\u53ca\u5982\u4f55\u5be6\u73fe ClusterIP \u9019\u7a2e\u985e\u578b\u7684 service. \u900f\u904e\u5c0d iptables \u7684\u63a2\u8a0e\u8207\u7814\u7a76, \u6211\u5011\u53ef\u4ee5\u7406\u89e3\u5230 ClusterIP \u672c\u8eab\u6703\u63d0\u4f9b\u4e00\u500b\u865b\u64ec\u7684 IP \u5730\u5740,\u63a5\u4e0b\u4f86\u53ea\u8981\u8ddf\u9019\u500b\u5730\u5740\u6709\u95dc\u7684\u5c01\u5305,\u90fd\u6703\u900f\u904e DNAT \u7684\u65b9\u5f0f\u9032\u884c\u8f49\u63db\u627e\u5230\u6700\u5f8c\u7684 Endpoint IP. \u81f3\u65bc\u5982\u4f55\u9078\u64c7\u7684\u90e8\u5206,\u5247\u662f\u900f\u904e\u6a5f\u7387\u7684\u65b9\u5f0f\u53bb\u5c0b\u627e. \u63a5\u4e0b\u4f86\u6211\u5011\u8981\u4f86\u63a2\u8a0e\u53e6\u5916\u4e00\u500b\u4e5f\u662f\u5f88\u5e38\u4f7f\u7528\u7684 kubernetes service \u985e\u578b, \u4e5f\u5c31\u662f NodePort. NodePort \u672c\u8eab\u5305\u542b\u4e86 ClusterIP \u7684\u6240\u6709\u529f\u80fd, \u6b64\u5916\u4e5f\u984d\u5916\u958b\u555f\u4e86\u4e00\u500b\u65b0\u7684\u5b58\u53d6\u65b9\u5f0f. \u900f\u904e\u76f4\u63a5\u5b58\u53d6\u7bc0\u9ede\u7684 IP \u5730\u5740\u914d\u4e0a\u4e00\u500b\u8a2d\u5b9a\u597d\u7684 Port, \u4e5f\u53ef\u4ee5\u5c07\u8a72\u5c01\u5305\u76f4\u63a5\u9001\u5230\u6700\u5f8c\u9762\u7684\u5bb9\u5668\u61c9\u7528\u7a0b\u5f0f. \u56e0\u6b64\u672c\u6587\u4e5f\u8981\u5ef6\u7e8c\u4e0a\u4e00\u7bc7\u7684\u601d\u8def,\u7e7c\u7e8c\u7814\u7a76 iptables \u7684\u898f\u5247\u4f86\u63a2\u8a0e NodePort \u5230\u5e95\u662f\u5982\u4f55\u5be6\u73fe\u7684","permalink":"/docs/techPost/2018/kubernetes-service-iii"},{"id":"techPost/2018/kubernetes-service-iiii","title":"[Kubernetes] How to Implement Kubernetes Service - SessionAffinity","description":"\u5728\u524d\u8ff0\u4e2d\u6211\u5011\u5df2\u7d93\u5b78\u904e\u4e86\u4ec0\u9ebc\u662f kubernetes service \u4ee5\u53ca\u5982\u4f55\u5be6\u73fe ClusterIP/NodePort \u7b49 service \u985e\u578b. \u900f\u904e\u5c0d iptables \u7684\u63a2\u8a0e\u8207\u7814\u7a76. \u63a5\u4e0b\u4f86\u8981\u7814\u7a76\u7684\u5247\u662f Service \u88e1\u9762\u7684\u4e00\u500b\u53c3\u6578, \u53eb\u505a SessionAffinity, \u6240\u8b02\u7684\u9023\u7dda\u89aa\u548c\u529b, \u900f\u904e\u8a72\u53c3\u6578\u7684\u8a2d\u5b9a,\u5e0c\u671b\u80fd\u5920\u8b93\u7b26\u5408\u7279\u5b9a\u689d\u4ef6\u7684\u9023\u7dda\u6700\u5f8c\u90fd\u6703\u9078\u7528\u5230\u76f8\u540c\u7684\u5f8c\u7aef\u61c9\u7528\u7a0b\u5f0f,\u76ee\u524d\u6709\u652f\u63f4\u7684\u9078\u9805\u662f ClinetIP, \u9019\u610f\u5473\u8005\u53ea\u8981\u9023\u7dda\u7684\u4f86\u6e90 IP \u5730\u5740\u662f\u76f8\u540c\u7684,\u6700\u5f8c\u90fd\u6703\u88ab\u5c0e\u5411\u5230\u76f8\u540c\u7684\u5bb9\u5668\u61c9\u7528\u7a0b\u5f0f. \u7136\u800c\u9019\u90e8\u5206\u5230\u5e95\u662f\u5982\u4f55\u5be6\u73fe\u7684, \u672c\u6587\u6703\u5148\u4ecb\u7d39\u4ec0\u9ebc\u53eb\u505a Connection. \u4e26\u4e14\u4ecb\u7d39 SessionAffinity \u7684\u4f7f\u7528\u65b9\u5f0f\u4ee5\u53ca\u4f7f\u7528\u5f8c\u7684\u7d50\u679c. \u6700\u5f8c\u4e00\u6a23\u900f\u904e\u76f8\u540c\u7684\u601d\u8def, \u85c9\u7531\u7814\u7a76 iptables \u7684\u898f\u5247\u4f86\u5b78\u7fd2\u5230 SessionAffinity \u8981\u5982\u4f55\u5b8c\u6210, \u540c\u6642\u4e5f\u53ef\u4ee5\u5b78\u7fd2\u5230 iptables \u8846\u591a\u9748\u6d3b\u7684\u4f7f\u7528\u65b9\u5f0f.","permalink":"/docs/techPost/2018/kubernetes-service-iiii"},{"id":"techPost/2018/netfilter-eiptables-iii","title":"[netfilter] Dig Into Docker Bridge Network By iptables/ebtables","description":"\u672c\u6587\u900f\u904e\u5c0d iptables/ebtables \u4f86\u8a2d\u5b9a\u76f8\u5c0d\u61c9\u7684\u898f\u5247\uff0c\u85c9\u7531\u9019\u4e9b\u898f\u5247\u4f86\u89c0\u5bdf\u5728 Docker Bridge Network \u7684\u7db2\u8def\u8a2d\u5b9a\u4e0b\uff0c\u4e0d\u540c\u60c5\u5883\u7684\u7db2\u8def\u50b3\u905e\u5be6\u969b\u4e0a\u6703\u53d7\u5230\u54ea\u4e9b iptables/ebtables \u898f\u5247\u7684\u5f71\u97ff\u3002\u9019\u4e9b\u60c5\u5883\u5305\u542b\u4e86\u5e38\u898b\u7684\u7528\u6cd5\uff0c\u8b6c\u5982\u5bb9\u5668\u8207\u5bb9\u5668\u4e4b\u9593\u540c\u7db2\u6bb5\u7684\u50b3\u8f38\uff0c\u5bbf\u4e3b\u6a5f\u900f\u904e\u5bb9\u5668IP\u4f4d\u5740\u76f4\u63a5\u9023\u7dda\uff0c\u751a\u81f3\u662f\u5916\u90e8\u7db2\u8def\u900f\u904e docker run -p xxx.xxx \u7684\u898f\u5247\u4f86\u63a5\u89f8\u5230\u5167\u90e8\u5bb9\u5668\u3002\u9019\u4e9b\u4e0d\u540c\u60c5\u5883\u7684\u7db2\u8def\u9023\u7dda\u727d\u626f\u5230\u95dc\u65bc Layer3 \u8def\u7531\uff0cLayer2 \u6a4b\u63a5 \u7b49\u4e0d\u540c\u65b9\u5f0f\u7684\u8655\u7406\uff0c\u56e0\u6b64\u5728 iptables/ebtables \u90fd\u6703\u6709\u4e0d\u540c\u7684\u8d70\u5411\u3002\u53ea\u8981\u80fd\u5920\u66f4\u4f73\u7684\u719f\u6089 iptables/ebtables \u7684\u7528\u6cd5\u8207\u898f\u5247\uff0c\u672a\u4f86\u6709\u9700\u8981\u89aa\u81ea\u8a2d\u5b9a\u76f8\u95dc\u898f\u5247\u6642\uff0c\u90fd\u80fd\u5920\u66f4\u7cbe\u6e96\u4e14\u5b89\u5168\u7684\u53bb\u9054\u5230\u60f3\u8981\u7684\u76ee\u7684\uff0c\u6e1b\u5c11\u76f2\u76ee\u731c\u6e2c\u7684\u6642\u9593\u8207\u82b1\u8cbb\u3002","permalink":"/docs/techPost/2018/netfilter-eiptables-iii"},{"id":"techPost/2018/netfilter-eiptables-ii","title":"[netfilter] Introduction to iptables","description":"\u900f\u904e\u77ad\u89e3 iptables \u898f\u5247\u7684\u56db\u5927\u7d44\u6210 Table/Chian/Match/Target \u4f86\u5b78\u7fd2 iptables \u7684\u898f\u5247\u542b\u7fa9\uff0c\u540c\u6642\u900f\u904e\u5716\u8868\u7684\u65b9\u5f0f\u4f86\u91d0\u6e05\u5c01\u5305\u5728 Linux Kernel \u50b3\u8f38\u904e\u7a0b\u4e2d\u53d7\u5230 iptables \u898f\u5247\u7684\u8655\u7406\u9806\u5e8f\u3002\u6700\u5f8c\u6703\u5c07 iptables \u4ee5\u53ca ebtables \u5169\u8005\u7684\u6d41\u7a0b\u5716\u6574\u5408\u5728\u4e00\u8d77\uff0c\u69cb\u5efa\u51fa\u4e00\u500b\u66f4\u5168\u9762\u7684\u5c01\u5305\u8f49\u9001\u6d41\u7a0b\u5716\uff0c\u65bc\u6b64\u6d41\u7a0b\u5716\u4e2d\u53ef\u4ee5\u89c0\u5bdf\u5230\u5c01\u5305\u5728 Routing/Bridging \u4e0d\u540c\u904e\u7a0b\u4e2d\uff0c\u662f\u5982\u4f55\u901a\u904e\u4e0d\u540c\u7684 ebtables/iptables \u898f\u5247\u7684\u8655\u7406\u3002 \u64c1\u6709\u9019\u4e9b\u8cc7\u8a0a\u80fd\u5920\u8b93\u4f60\u5c0d\u7cfb\u7d71\u4e0a\u7684 iptables/ebtables \u6709\u66f4\u5168\u9762\u6027\u7684\u7406\u89e3\u5176\u529f\u7528\u4ee5\u53ca\u767c\u751f\u6642\u6a5f","permalink":"/docs/techPost/2018/netfilter-eiptables-ii"},{"id":"techPost/2019/iptables-masquerade","title":"Linux NAT Masquerade \u7814\u7a76(\u4e0a)","description":"\u672c\u7bc7\u6587\u7ae0\u900f\u904e\u95b1\u8b80\u539f\u59cb\u78bc\u7684\u65b9\u5f0f\u4f86\u5b78\u7fd2 MASQUERADE \u7684\u904b\u4f5c\u6a21\u5f0f\uff0c\u800c MASQUERADE \u5247\u662f\u88ab\u5ee3\u70ba\u4f7f\u7528\u7684 SOURCE NAT \u6a21\u7d44\u3002\u4f5c\u70ba\u4e00\u500b IPTABLES \u7684\u64f4\u5145\u6a21\u7d44\uff0c\u900f\u904e\u89c0\u5bdf\u539f\u59cb\u78bc\u7684\u65b9\u5f0f\u53ef\u4ee5\u5b78\u7fd2\u5230\u662f\u5982\u4f55\u8655\u7406\u76f8\u95dc\u7684\u53c3\u6578\u751a\u81f3\uff0c\u9078\u64c7\u4f86\u6e90 IP \u5730\u5740\u4ee5\u53ca\u4f86\u6e90\u9023\u63a5\u57e0\u7b49\u76f8\u95dc\u884c\u70ba","permalink":"/docs/techPost/2019/iptables-masquerade"},{"id":"techPost/2020/iptables-masquerade-handson","title":"Linux NAT Masquerade \u7814\u7a76(\u4e0b)","description":"\u672c\u7bc7\u6587\u7ae0\u900f\u904e\u4fee\u6539 MASQUERADE Kernel Module \u539f\u59cb\u78bc\u7684\u65b9\u5f0f\u4f86\u89c0\u5bdf\u7cfb\u7d71\u7684\u8b8a\u5316\uff0c\u5c08\u6ce8\u65bc\u7576 NAT \u529f\u80fd\u57f7\u884c\u524d\u5f8c\uff0c conntrack \u9019\u500b\u7d50\u69cb\u7684\u6539\u8b8a\u3002\u900f\u904e\u4e0d\u540c\u7684\u5be6\u9a57\u8207\u74b0\u5883\u4f86\u89c0\u5bdf 1)\u591a\u91cd IP \u7684\u60c5\u6cc1\u4e0b\u600e\u9078\u64c7 2)\u6307\u5b9a\u4e0d\u540c\u53c3\u6578\u6642\uff0c\u9023\u63a5\u57e0\u7684\u8b8a\u5316\u3002\u6b64\u5916\u70ba\u4e86\u7c21\u5316\u6574\u9ad4\u64cd\u4f5c\u904e\u7a0b\uff0c\u5c07\u6574\u500b\u5be6\u9a57\u74b0\u5883\u90fd\u900f\u904e Vagrant \u6253\u5305\u6210\u70ba\u4e00\u500b\u53ef\u91cd\u8907\u57f7\u884c\u7684\u74b0\u5883\uff0c\u4e26\u4e14\u4e5f\u6e96\u5099\u597d\u53ef\u4ee5\u7de8\u8b6f Kernel Module \u7684\u74b0\u5883\u8207\u6307\u4ee4\u3002","permalink":"/docs/techPost/2020/iptables-masquerade-handson"},{"id":"techPost/2016/netfilter-nfqueue","title":"NFQUEUE drop UDP packets","description":"\u63a2\u8a0e NFQUEUE + iptables \u7d50\u5408\u5f8c\u4e1f\u68c4 UDP \u5c01\u5305\u7684\u554f\u984c","permalink":"/docs/techPost/2016/netfilter-nfqueue"},{"id":"techPost/2020/iptables-1","title":"\u521d\u63a2 IPTABLES \u6d41\u52d5\u4e4b\u8def - \u4ee5 Docker \u70ba\u7bc4\u4f8b","description":"\u672c\u6587\u900f\u904e IPTABLES/EBTABLES \u7684\u898f\u5247\u8207 Docker Container \u7684\u74b0\u5883\u4f86\u89c0\u5bdf\u4e0d\u540c\u60c5\u6cc1\u4e0b\u7684\u5c01\u5305\u6d41\u5411\uff0c\u4e3b\u8981\u662f\u5e6b\u7dda\u4e0a\u8b70\u7a0b\u9032\u884c\u7684\u91cd\u9ede\u6574\u7406\uff0c\u4e3b\u8981\u5167\u5bb9\u90fd\u5728\u5f71\u7247\u4e2d\u3002","permalink":"/docs/techPost/2020/iptables-1"}]}')}}]);