"use strict";(self.webpackChunkhwchiu=self.webpackChunkhwchiu||[]).push([[135],{92452:e=>{e.exports=JSON.parse('{"label":"CSI","permalink":"/docs/tags/csi","allTagsPath":"/docs/tags","count":5,"items":[{"id":"techPost/2019/iThome_Challenge/csi","title":"Container Storage Interface \u57fa\u672c\u4ecb\u7d39","description":"\u672c\u6587\u958b\u59cb\u53c8\u662f\u5d84\u65b0\u7684\u4e00\u7bc7\uff0c\u958b\u59cb\u63a2\u8a0e\u4e5f\u662f\u975e\u5e38\u91cd\u8981\u7684\u5132\u5b58\u8cc7\u6e90\uff0c\u5132\u5b58\u65b9\u9762\u4e5f\u6709\u76f8\u95dc\u7684\u6a19\u6e96\u518d\u767c\u5c55\uff0c\u5176\u76ee\u7684\u8207 CRI \u4ee5\u53ca CNI \u4e00\u81f4\uff0c\u90fd\u662f\u5e0c\u671b\u900f\u904e\u6a19\u6e96\u5316\u63a5\u53e3\u80fd\u5920\u4f7f\u5f97\u7b2c\u4e09\u65b9\u7684\u89e3\u6c7a\u65b9\u6848\u958b\u767c\u66f4\u52a0\u6d3b\u7d61\u4e14\u6d41\u66a2\uff0c\u672c\u6587\u6703\u958b\u59cb\u4f86\u4ecb\u7d39\u5132\u5b58\u65b9\u9762\u7684\u57fa\u672c\u6982\u5ff5\u4ee5\u53ca\u70ba\u4ec0\u9ebc\u9700\u8981 Container Storage Interface","permalink":"/docs/techPost/2019/iThome_Challenge/csi"},{"id":"techPost/2019/iThome_Challenge/csi-ii","title":"Container Storage Interface \u6a19\u6e96\u4ecb\u7d39","description":"\u672c\u6587\u91dd\u5c0d Container Storage Interface \u7684\u6a19\u6e96\u9032\u884c\u4ecb\u7d39\uff0c\u63a2\u8a0e CSI \u672c\u8eab\u6a19\u6e96\u6db5\u84cb\u7684\u7bc4\u570d\u8207\u6d41\u7a0b\uff0c\u4ee5\u53ca\u90e8\u7f72\u4e0a\u7684\u4e00\u4e9b\u5e38\u898b\u505a\u6cd5\uff0c\u5305\u542b\u591a\u7a2e\u670d\u52d9\u5668\u5404\u81ea\u7684\u89d2\u8272\u8207\u5730\u4f4d\u3002","permalink":"/docs/techPost/2019/iThome_Challenge/csi-ii"},{"id":"techPost/2019/iThome_Challenge/csi-iii","title":"Container Storage Interface \u8207 kubernetes","description":"\u672c\u7bc7\u6587\u7ae0\u8457\u91cd\u65bc Kubernetes \u672c\u8eab\u8207 CSI \u7684\u95dc\u4fc2\uff0c\u4e00\u76f4\u4ee5\u4f86 kubernetes \u751a\u81f3\u5230 1.16 \u4f86\u8aaa\uff0c CSI \u90fd\u4e0d\u662f\u4e00\u500b\u5fc5\u9700\u7684\u8a2d\u5b9a\uff0c\u53cd\u800c\u662f\u4f9d\u8cf4\u5927\u91cf in-tree \u7684\u6574\u5408\u7a0b\u5f0f\u78bc\u4f86\u8655\u7406\u5404\u5f0f\u5404\u6a23\u7684\u5132\u5b58\u5f8c\u7aef\uff0c\u9019\u96d6\u7136\u4f7f\u7528\u65b9\u4fbf\u4f46\u662f\u5c0d\u65bc\u5f8c\u7e8c\u7684\u64f4\u5145\u6027\uff0c\u7dad\u8b77\u6027\uff0c\u5f48\u6027\u90fd\u5e36\u4f86\u7684\u5f88\u5927\u7684\u5371\u5bb3\u3002\u4e5f\u56e0\u6b64 CSI \u624d\u6709\u8a95\u751f\u7684\u6a5f\u6703\uff0c\u6240\u4ee5\u5c0d\u65bc\u9577\u671f\u4f7f\u7528 in-tree \u7684\u7ba1\u7406\u4eba\u54e1\u4f86\u8aaa\uff0c\u52e2\u5fc5\u6709\u4e00\u5929\u8981\u8f49\u79fb\u5230 CSI\u624d\u6709\u6a5f\u6703\u60f3\u5230\u65b0\u7684\u529f\u80fd\u4ee5\u53ca\u76f8\u95dc\u529f\u80fd\u7684\u4fee\u5fa9","permalink":"/docs/techPost/2019/iThome_Challenge/csi-iii"},{"id":"techPost/2019/iThome_Challenge/csi-nfs","title":"Container Storage Interface(CSI) - NFS \u521d\u9ad4\u9a57","description":"\u524d\u8ff0\u5206\u4eab\u4e86\u95dc\u65bc\u4e0d\u5c11 Container Storage Interface \u7684\u4e8b\u60c5\uff0c\u63a5\u4e0b\u4f86\u6211\u5011\u76f4\u63a5\u4f7f\u7528 NFS \u4f5c\u70ba\u7bc4\u4f8b\uff0c\u4f86\u770b\u770b\u8981\u5982\u4f55\u900f\u904e CSI \u7684\u67b6\u69cb\u4f86\u639b\u8f09 NFS \u5230 kubernetes cluster \u4e2d\u4f9b\u904b\u7b97\u8cc7\u6e90\u4f7f\u7528","permalink":"/docs/techPost/2019/iThome_Challenge/csi-nfs"},{"id":"techPost/2019/iThome_Challenge/csi-other","title":"CSI \u96dc\u8ac7","description":"\u672c\u7bc7\u6587\u7ae0\u4f5c\u70ba Storage \u7684\u6700\u5f8c\u4e00\u584a\uff0c\u5c31\u6c92\u6709\u7279\u5225\u91dd\u5c0d\u4e3b\u984c\u63a2\u8a0e\uff0c\u53cd\u800c\u662f\u804a\u804a\u9592\u804a\u4e00\u4e9b Storage \u65b9\u9762\u7684\u8b70\u984c\uff0c\u9806\u4fbf\u804a\u804a Mount \u9019\u500b\u6771\u897f\u9664\u4e86\u5e38\u898b\u7684\u529f\u80fd\u4e4b\u5916\uff0c\u9084\u6709\u4ec0\u9ebc\u53c3\u6578\u53ef\u4ee5\u4f7f\u7528\uff0c\u4e26\u4e14\u6709\u4ec0\u9ebc\u6a23\u7684\u6548\u679c\u3002","permalink":"/docs/techPost/2019/iThome_Challenge/csi-other"}]}')}}]);