"use strict";(self.webpackChunkhwchiu=self.webpackChunkhwchiu||[]).push([[2813],{44228:e=>{e.exports=JSON.parse('{"label":"DevicePlugin","permalink":"/docs/tags/device-plugin","allTagsPath":"/docs/tags","count":4,"items":[{"id":"techPost/2019/iThome_Challenge/k8s-device-plugin","title":" Device Plugin - Introduction","description":"\u9664\u4e86\u4e09\u5927\u6a19\u6e96 CRI, CNI, CSI \u4e4b\u5916\uff0c kubernetes \u672c\u8eab\u4e5f\u6709\u81ea\u884c\u6642\u505a\u4e00\u4e9b\u65b9\u5f0f\u4f86\u64f4\u5145\u6574\u500b\u53e2\u96c6\u7684\u529f\u80fd\uff0c\u800c\u672c\u6587\u8981\u4ecb\u7d39\u7684\u5c31\u662f Device Plugin \u9019\u500b\u64f4\u5145\u529f\u80fd\uff0c\u9019\u500b\u529f\u80fd\u6700\u8457\u540d\u7684\u4f7f\u7528\u65b9\u5f0f\u6211\u60f3\u5c31\u662f GPU\u904b\u7b97\u4e86\uff0c\u900f\u904e Device Plugin, \u53ef\u4ee5\u8b93\u4e0d\u540c GPU \u5ee0\u5546\u90fd\u80fd\u5920\u81ea\u884c\u5be6\u4f5c\u76f8\u95dc\u7684\u65b9\u5f0f\u628a GPU \u8ddf\u904b\u7b97\u8cc7\u6e90\u7d50\u5408\u4e26\u4e14\u900f\u904e kubernetes\u5167\u5efa\u7684 scheduler \u7b49\u6709\u6548\u5730\u4f7f\u7528\u9019\u4e9b\u8cc7\u6e90\u3002","permalink":"/docs/techPost/2019/iThome_Challenge/k8s-device-plugin"},{"id":"techPost/2019/iThome_Challenge/k8s-device-plugin-implement","title":"Device Plugin - Implementation","description":"\u672c\u7bc7\u6587\u7ae0\u6703\u5c31 device plugin \u672c\u8eab\u7684\u67b6\u69cb\u4f86\u63a2\u8a0e\u5982\u4f55\u5be6\u505a\u4e00\u500b device plugin \u7684\u89e3\u6c7a\u65b9\u6848\uff0c\u5f9e\u5176\u6e9d\u901a\u4ecb\u9762\u5230\u904b\u4f5c\u6d41\u7a0b\u9032\u884c\u63a2\u8a0e\uff0c\u900f\u904e\u9019\u4e9b\u7406\u89e3\u8207\u8a0e\u8ad6\u6703\u5c0d device plugin \u6709\u66f4\u591a\u7684\u4e86\u89e3","permalink":"/docs/techPost/2019/iThome_Challenge/k8s-device-plugin-implement"},{"id":"techPost/2019/iThome_Challenge/k8s-device-plugin-rdma","title":"Kubernetes - Device Plugin - RDMA","description":"\u9019\u7bc7\u8981\u4f86\u8ddf\u5927\u5bb6\u4ecb\u7d39\u4ec0\u9ebc\u662f Remote Directly Memory Access(RDMA)\uff0c\u5f9e\u9019\u500b\u88dd\u7f6e\u7684\u6982\u5ff5\u4ecb\u7d39\u4f86\u5b78\u7fd2\u8a72\u88dd\u7f6e\u7684\u4f7f\u7528\u60c5\u5883\uff0c\u4e26\u4e14\u5957\u7528\u5230 kubernetes device plugin \u7684\u6846\u67b6\u4e2d\u53ef\u4ee5\u600e\u9ebc\u4f7f\u7528\u3002","permalink":"/docs/techPost/2019/iThome_Challenge/k8s-device-plugin-rdma"},{"id":"techPost/2019/iThome_Challenge/k8s-device-plugin-sriov","title":"Kubernetes - Device Plugin - SRIOV","description":"\u9019\u7bc7\u8981\u4f86\u8ddf\u5927\u5bb6\u4ecb\u7d39\u4ec0\u9ebc\u662f Single Root Input Output Virtualization (SR-IOV)\uff0c\u4f5c\u70ba\u4e00\u500b OpenStack \u4e16\u754c\u4e2d\u5c31\u5927\u91cf\u88ab\u4f7f\u7528\u7684\u88dd\u7f6e\uff0c\u5230\u5e95\u5176\u63d0\u4f9b\u4ec0\u9ebc\u6a23\u7684\u529f\u80fd\uff0c\u4ee5\u53ca\u70ba\u4ec0\u9ebc kubernetes \u4e2d\u6703\u9700\u8981\u9019\u500b\u88dd\u7f6e\uff0c\u4e26\u4e14\u4ec0\u9ebc\u60c5\u5883\u4e0b\u9700\u8981\u4f7f\u7528\u9019\u500b\u88dd\u7f6e\u3002","permalink":"/docs/techPost/2019/iThome_Challenge/k8s-device-plugin-sriov"}]}')}}]);