"use strict";(self.webpackChunkhwchiu=self.webpackChunkhwchiu||[]).push([[69504],{98766:e=>{e.exports=JSON.parse('{"label":"TensorFlow","permalink":"/docs/tags/tensor-flow","allTagsPath":"/docs/tags","count":1,"items":[{"id":"2018/paper-tensorflow-with-rdma","title":"[\u8ad6\u6587\u5c0e\u8b80] - Towards Zero Copy Dataflows using RDMA","description":"\u672c\u6587\u5c6c\u65bc\u8ad6\u6587\u5c0e\u8b80\u7cfb\u5217\uff0c\u9019\u6b21\u91dd\u5c0d\u7684\u662f\u9ad8\u901f\u7db2\u8def(RDMA)\u7684\u61c9\u7528\uff0c\u4f86\u6e90\u662f SICCOM 2017 \u6703\u8b70\u4e0a\u3002\u9019\u7bc7\u6587\u7ae0\u6709\u8da3\u7684\u5730\u65b9\u5728\u65bc\u4ed6\u4e0d\u662f\u55ae\u7d14\u7684\u4ecb\u7d39\u67b6\u69cb\uff0c\u800c\u662f\u900f\u904e\u4e00\u500b\u5be6\u969b\u7684\u61c9\u7528\u7a0b\u5f0f\u4f86\u95e1\u8ff0\u7576\u8a72\u61c9\u7528\u7a0b\u5f0f\u642d\u914d\u4e0a RDMA \u5f8c\u7372\u5f97\u4e86 Zero Copy \u7684\u7279\u8272\uff0c\u5728\u6b64\u7279\u8272\u52a0\u6301\u4e0b\uff0c\u539f\u5148\u61c9\u7528\u7a0b\u5f0f\u7684\u6548\u80fd\u63d0\u5347\u4e86\u591a\u5c11\u3002\u672c\u6587\u7684\u6a19\u984c\u662f Towards Zero Copy Dataflows using RDMA, \u5176\u5167\u5bb9\u8ddf AI \u7684\u8a13\u7df4\u904e\u7a0b\u6709\u95dc\uff0c\u63a1\u7528\u4e86\u7531 Google \u958b\u6e90\u7684\u8a13\u7df4\u6846\u67b6\uff0c Ternsorflow, \u4e26\u4e14\u5206\u6790\u4e86\u5728\u539f\u5148\u5206\u6563\u5f0f\u7684\u8a13\u7df4\u6a21\u578b\u4e2d\uff0c\u8cc7\u6599\u4e0d\u8ad6\u5728 CPU/GPU UserSpace/KernelSpace \u751a\u81f3\u7bc0\u9ede\u9593\u90fd\u6709\u5927\u91cf\u7684\u8cc7\u6599\u8907\u88fd\u884c\u70ba\u3002\u900f\u904e RDMA \u7684\u5e6b\u5fd9\u6e1b\u5c11\u4e86\u9019\u4e9b\u884c\u70ba\u6700\u5f8c\u8b49\u660e\u4e86\u6574\u9ad4\u5206\u6563\u5f0f\u8a13\u7df4\u7684\u6642\u9593\u5927\u5e45\u5ea6\u7e2e\u77ed\uff0c\u662f\u500b\u975e\u5e38\u6709\u8da3\u7684\u77ed\u6587.","permalink":"/docs/2018/paper-tensorflow-with-rdma"}]}')}}]);