"use strict";(self.webpackChunkhwchiu=self.webpackChunkhwchiu||[]).push([[90566],{56629:e=>{e.exports=JSON.parse('{"label":"CRI","permalink":"/docs/tags/cri","allTagsPath":"/docs/tags","count":5,"items":[{"id":"2019/iThome_Challenge/k8s-cri-o","title":"Container Runtime - CRI-O","description":"\u672c\u6587\u8981\u7279\u5225\u4ecb\u7d39\u57fa\u65bc Kubernetes \u6240\u958b\u767c\u7684 CRI \u89e3\u6c7a\u65b9\u6848 CRI-O, CRI-O \u6240\u6709\u7684\u529f\u80fd\u90fd\u662f\u70ba\u4e86 kubernetes \u800c\u8a2d\u8a08\uff0c\u4e0d\u591a\u4e0d\u5c11\u525b\u525b\u597d\uff0c\u5b8c\u5168\u6700\u4f73\u5316\u65bc kubernetes\u3002\u6b64\u5916\u7531\u65bc\u4e0d\u9700\u8981\u501a\u8cf4\u592a\u591a\u7b2c\u4e09\u65b9\u7684\u5143\u4ef6\uff0c\u8b6c\u5982 docker, containerd \u7b49\uff0c\u4f7f\u7528 CRI-O \u53ef\u4ee5\u6e1b\u5c11\u5143\u4ef6\u4e4b\u9593 IPC \u6e9d\u901a\u7684\u6b21\u6578\u9032\u800c\u63d0\u5347\u6574\u9ad4\u7684\u6548\u7387\u3002 \u672c\u6587\u4e3b\u8981\u6703\u4ecb\u7d39 CRI-O \u7684\u6982\u5ff5\u4e26\u4e14\u900f\u904e\u5be6\u969b\u5b89\u88dd\u7684\u65b9\u5f0f\u4ecb\u7d39\u5982\u4f55\u5e6b kubernetes \u62bd\u63db runtime \u6210 CRI-O","permalink":"/docs/2019/iThome_Challenge/k8s-cri-o"},{"id":"2019/iThome_Challenge/container-runtime-security-container","title":"Container Runtime - Security Container","description":"\u6709\u9452\u65bc CRI \u7684\u6a19\u6e96\u67b6\u69cb\uff0c\u5404\u5f0f\u5404\u6a23\u7684\u91dd\u5c0d\u4e0d\u540c\u76ee\u6a19\u7684\u5c08\u6848\u90fd\u80fd\u5920\u6574\u5408\u5230 Kubernetes \u4e2d\uff0c\u800c\u672c\u6587\u8981\u4ecb\u7d39\u7684\u5169\u500b\u5c08\u6848\u5206\u5225\u662f gVisor \u4ee5\u53ca kata-container, \u9019\u5169\u500b\u5c08\u6848\u90fd\u662f\u57fa\u65bc\u5b89\u5168\u6027\u8003\u91cf\u800c\u958b\u767c\u7684 OCI Runtine\uff0c\u8207\u5176\u76f8\u540c\u5730\u4f4d\u7684\u5c31\u662f\u6700\u77e5\u540d\u7684 OCI Runtime, runc\u3002 \u672c\u6587\u6703\u4ecb\u7d39\u9019\u5169\u500b\u5c08\u6848\u7684\u6982\u5ff5\u4ee5\u53ca\u76ee\u6a19\uff0c\u4e26\u4e14\u8a0e\u8ad6\u5176\u5be6\u4f5c\u67b6\u69cb\u4f86\u6bd4\u8f03\u9019\u5169\u7a2e\u4e0d\u540c\u7684\u65b9\u5f0f\u662f\u5982\u4f55\u9054\u5230\u5b89\u5168\u7684\u6548\u679c","permalink":"/docs/2019/iThome_Challenge/container-runtime-security-container"},{"id":"2019/iThome_Challenge/container-runtime-vm","title":"Container Runtime - Virtual Machine","description":"\u672c\u6587\u4f5c\u70ba CRI \u7cfb\u5217\u6587\u7ae0\u7684\u6700\u5f8c\u4e00\u7bc7\u6587\u7ae0\uff0c\u8981\u4f86\u8ddf\u5927\u5bb6\u5206\u4eab\u4e00\u500b\u6bd4\u8f03\u5c11\u898b\u7684\u7528\u6cd5\uff0c\u900f\u904e kubernetes \u7ba1\u7406 Pod \u7684\u65b9\u5f0f\u4f86\u7ba1\u7406 Virtual Machine\uff0c\u5e95\u5c64\u6240\u6709\u904b\u884c\u7684\u670d\u52d9\u90fd\u662f\u57fa\u65bc Virtual Machine \u53bb\u8dd1\uff0c\u4f46\u662f\u5c0d\u65bc Kubernetes \u4f86\u8aaa\u5b8c\u5168\u4e0d\u77e5\u60c5\uff0c\u4f9d\u7136\u8a8d\u70ba\u662f\u4e00\u500b Pod \u7684\u5f62\u5f0f\u3002 \u9019\u90e8\u5206\u4f9d\u8cf4\u91cd\u65b0\u64b0\u5beb\u4e00\u500b\u7b26\u5408 CRI \u6a19\u6e96\u61c9\u7528\u7a0b\u5f0f\u4f86\u5c0d\u63a5 kubelet, \u4e26\u4e14\u5e95\u4e0b\u6240\u6709\u7684\u904b\u4f5c\u90fd\u4f7f\u7528 virtual machine  \u4f86\u64cd\u4f5c\uff0c\u56e0\u6b64\u9019\u500b\u7bc4\u4f8b\u5c31\u5b8c\u5168\u4e0d\u6703\u6709 OCI \u7684\u5b58\u5728\uff0c\u56e0\u70ba\u662f\u771f\u6b63\u7684 virtual machine, \u800c\u975e container.","permalink":"/docs/2019/iThome_Challenge/container-runtime-vm"},{"id":"2019/iThome_Challenge/k8s-cri-i","title":"Kubernetes & CRI (I)","description":"\u672c\u7bc7\u6587\u7ae0\u6703\u958b\u59cb\u63a2\u8a0e kubernetes Container Runtime Interface \u7684\u6982\u5ff5\u8207\u8a2d\u8a08\uff0c\u4e26\u4e14\u8a0e\u8ad6\u5176\u9918 OCI \u7684\u95dc\u4fc2\uff0c\u6700\u5f8c\u4ecb\u7d39\u4e86 Docker/Containerd \u9019\u4e9b\u4e0d\u7684 Container \u89e3\u6c7a\u65b9\u6848\u662f\u5982\u4f55\u65bc Kubernetes \u5171\u5b58\u7684","permalink":"/docs/2019/iThome_Challenge/k8s-cri-i"},{"id":"2019/iThome_Challenge/k8s-cri-ii","title":"Kubernetes & CRI (II) - containerd","description":"\u672c\u6587\u4ecb\u7d39\u4e86 `containerd \u7684\u6982\u5ff5\uff0c\u4e26\u4e14\u5617\u8a66\u5efa\u7f6e\u4e00\u500b\u57fa\u65bc containerd \u7684 kubernetes cluster, \u8b93\u5927\u5bb6\u9ad4\u9a57\u770b\u770b\u4f48\u6a01 docker \u4e5f\u53ef\u4ee5\u904b\u884c kubernetes \u7684\u4f7f\u7528\u65b9\u5f0f\uff0c\u540c\u6642\u4e5f\u53ef\u4ee5\u5b78\u7fd2\u5230\u662f\u5982\u4f55\u900f\u904e\u8a2d\u5b9a\u4f86\u6539\u8b8a kubernetes \u9019\u4e00\u9023\u4e32\u7684\u884c\u70ba\u7684\u3002","permalink":"/docs/2019/iThome_Challenge/k8s-cri-ii"}]}')}}]);